mapper.select:
INSERT INTO industry ( industry_code, industry_name, industry_description ) VALUES
( #{industry.industryCode}, #{industry.industryName}, #{industry.industryDescription} )
ON CONFLICT (industry_code) DO UPDATE SET industry_name = EXCLUDED.industry_name, industry_description = EXCLUDED.industry_description RETURNING *;

mapper.insert:
INSERT INTO company_industry(company_id, industry_id)
VALUES

    (
    #{company.id},
    #{industry.id}
    )

ON CONFLICT
DO NOTHING;

mapper.select:
INSERT INTO tax_authority (
    authority_code,
    authority_name
) VALUES
(
    #{authorityCode},
    #{authorityName}
) ON CONFLICT (authority_code, authority_name)
    DO UPDATE
  SET
      authority_code = EXCLUDED.authority_code,
      authority_name = EXCLUDED.authority_name
  RETURNING *;
